      element: <HomeView />,
    },
    {
      path: "/about",
      element: <About />,
    },
  ],
  {
    // Remove trailing slash for basename
    basename: baseUrl.endsWith("/") ? baseUrl.slice(0, -1) : baseUrl,
  }
);

function App() {
